var nodeMailer = require('nodemailer');

//configuration set krni padegi ki kis password mail se kaha mail send krni hai
var transport = nodeMailer.createTransport({ //yeh function object{} leta hai aur isme hume 2-3 chize deni hoti hai
    host: 'smtp.gmail.com', //gmail se mail send krhe isliye host yeh rahega. gmail ka host yahi hai
    port: 587, //port bhi fix haii
    secure: false, //secure hai ki nhi
    requireTLS: true,
    auth:{ //auth ek object leta hai jisme 2 chize hoti hai
        user: "sumitsantape@gmail.com",
        pass: " "
    }

});
var mailOptions= {
        from : 'sumitsantape@gmail.com',
        to : 'santapesumit@gmail.com',
        subject : 'Nodejs demo',
        text: 'Hello, how are you?'
    }


transport.sendMail(mailOptions, function(error, info){
    if(error){
        console.log(error);
    }else{
        console.warn("Email has been sent", info.response);
    }
})